import React, { useRef, useState } from "react";
import "../Appointment/appont.css";
import { Dialog } from "primereact/dialog";
import { Toast } from "primereact/toast";
const Appoinment = () => {
  const [visible, setVisible] = useState(false);
  let Procedures = (e) => {
    e.preventDefault();
    setVisible(true);
    console.log("solg");
  };
  const toastCenter = useRef(null);

  const show = () => {
    toastCenter.current.show({
      severity: "info",
      summary: "Info",
      detail: "Message Content",
    });
  };

  return (
    <>
      <h1 className="h1">Book your Appoinment</h1>
      <div className="appcontainer">
        <form>
          <lable>name:</lable>
          <input type="text" />
          <br />
          <lable>age</lable>
          <input type="number" />
          <br />
          <lable>Email</lable>
          <input type="email" />
          <br />
          <lable>cause of vist</lable>
          <select placeholder="select ">
            {/* <option>ss</option> */}
            <option value="1">fever</option>
            <option value="2">cough</option>
            <option value="3">body pain</option>
            <option value="4">other</option>
          </select>
          <br />
          <div className="card flex justify-content-center">
            <Dialog
              header="Pick a Slot"
              visible={visible}
              style={{
                width: "50vw",
                border: "2px solid black",
                backgroundColor: "white",
                boxShadow: "5px 5px 15px rgba(0, 0, 0, 0.75)",
              }}
              onHide={() => setVisible(false)}
            >
              <div className="dialog">
                <input type="date" className="input" />
                <input type="time" className="input" />
                <div style={{ width: "100%" }}>
                  <button className="slot me-3">Anytime</button>
                  <button className="slot">Pick a Slot</button>
                </div>

                <div style={{ width: "100%" }}>
                  <Toast ref={toastCenter} position="center" />
                  <button className="formsub" onClick={show} label=" info">
                    submit
                  </button>
                </div>
              </div>
            </Dialog>
          </div>
        </form>
        <button onClick={Procedures} className="formsub">
          proceed
        </button>
      </div>
    </>
  );
};

export default Appoinment;
