import React, { useRef, useState } from "react";
import "../Appointment/appont.css";
import { Dialog } from "primereact/dialog";
import { Toast } from "primereact/toast";
import { useNavigate } from "react-router-dom";
import axios from "axios";
const Appoinment = () => {
  const navigate = useNavigate();
  const [visible, setVisible] = useState(false);
  const [disable, setdisable] = useState("typing");

  const [appodata, setappodata] = useState({
    name: "",
    age: "",
    email: "",
    causeofvisit: "",
    date: "",
    time: "",
  });
  console.log("ghjk", appodata);

  let Procedures = (e) => {
    e.preventDefault();
    setVisible(true);
  };
  const toastCenter = useRef(null);
  let handleinput = (e) => {
    const { name, value } = e.target;
    setappodata({ ...appodata, [name]: value });
  };

  const show = (e, req) => {
    e.preventDefault();
    setdisable("submited");

    const appoinput = {
      name: req.body.name,
      age: appodata.age,
      email: appodata.email,
      causeofvisit: appodata.causeofvisit,
      date: appodata.date,
      time: appodata.time,
    };
    axios
      .post("http://localhost:3002/appointment/appointment", appoinput)
      .then((res) => {
        console.log(res);
      })
      .catch((err) => {
        console.log(err);
      });

    // console.log(appoinput);

    toastCenter.current.show({
      severity: "info",
      summary: "Info",
      detail: "Your appoinment was Booked",
    });
  };

  return (
    <>
      <h1 className="h1">Book your Appoinment</h1>
      <div className="appcontainer">
        <form onSubmit={show}>
          <lable>name</lable>
          <input
            type="text "
            name="name"
            value={appodata.name}
            onChange={handleinput}
          />
          <br />
          <lable>age</lable>
          <input
            type="number"
            name="age"
            value={appodata.age}
            onChange={handleinput}
          />
          <br />
          <lable>Email</lable>
          <input
            type="email"
            name="email"
            value={appodata.email}
            onChange={handleinput}
          />
          <br />
          <lable>cause of vist</lable>
          <select
            placeholder="select"
            name="causeofvisit"
            value={appodata.causeofvisit}
            onChange={handleinput}
          >
            <option value=""></option>
            <option>fever</option>
            <option>cough</option>
            <option>body pain</option>
            <option>other</option>
          </select>
          <br />
          <div className="card flex justify-content-center">
            <Dialog
              header="Pick a Slot"
              visible={visible}
              style={{
                width: "50vw",
                border: "2px solid black",
                backgroundColor: "white",
                boxShadow: "5px 5px 15px rgba(0, 0, 0, 0.75)",
              }}
              onHide={() => setVisible(false)}
            >
              <div className="dialog">
                <input
                  type="date"
                  className="input"
                  name="date"
                  value={appodata.date}
                  onChange={handleinput}
                />
                <input
                  type="time"
                  className="input"
                  name="time"
                  value={appodata.time}
                  onChange={handleinput}
                />
                <div style={{ width: "100%" }}>
                  <button className="slot me-3">Anytime</button>
                  <button className="slot">Pick a Slot</button>
                </div>

                <div style={{ width: "100%" }}>
                  <Toast ref={toastCenter} position="center" />
                  <button className="formsub" onClick={show} label="Top Center">
                    submit
                  </button>
                </div>
              </div>
            </Dialog>
          </div>
        </form>
        <button
          onClick={Procedures}
          className="formsub"
          disabled={
            appodata.name.length === 0 ||
            appodata.age.length === 0 ||
            appodata.email.length === 0 ||
            appodata.causeofvisit.length === 0 ||
            disable === "submited"
          }
        >
          proceed
        </button>
      </div>
    </>
  );
};

export default Appoinment;
