    setappodata({ ...appodata, [name]: value });
  };

  const show = (e) => {
    e.preventDefault();
    setdisable("submited");
    if (
      appodata.name === "" ||
      appodata.age === "" ||
      appodata.email === "" ||
      appodata.cause === "" ||
