            name="email"
            value={appodata.email}
            onChange={handleinput}
          />
          <br />
          <lable>cause of vist</lable>
          <select
            placeholder="select"
            name="cause"
            value={appodata.cause}
            onChange={handleinput}
          >
            <option value=""></option>
            <option>fever</option>
            <option>cough</option>
            <option>body pain</option>
            <option>other</option>
