                width: "50vw",
                border: "2px solid black",
                backgroundColor: "white",
                boxShadow: "5px 5px 15px rgba(0, 0, 0, 0.75)",
              }}
              onHide={() => setVisible(false)}
            >
              <div className="dialog">
                <input
                  type="date"
                  className="input"
                  name="date"
