import React, { useState } from "react";
import { Link } from "react-router-dom";
// import PaginatorBasicDemo from "./Admin Componts/Table";
import { useLocation } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import "./admin.css";
import Optab from "./Optab";
import logo from "../../images/hospital_logo.png";

const AdminPage = () => {
  const location = useLocation();
  // console.log(location.state);
  const navigate = useNavigate();

  function direct() {
    navigate("/Login");
  }
  const [isCheckedIn, setIsCheckedIn] = useState(false);
  const [checkInTime, setCheckInTime] = useState("");
  const [checkOutTime, setCheckOutTime] = useState(null);
  const handleButtonClick = () => {
    if (isCheckedIn) {
      const handleCheckIn = () => {
        let date = new Date();
        let time = date.toLocaleTimeString();
        let dates =
          date.getFullYear() +
          "-" +
          (date.getMonth() + 1) +
          "-" +
          date.getDate();
        let currentTime = [
          {
            time: time,
            date: dates,
          },
        ];
        setCheckInTime(currentTime);
      };
    } else {
      const handleCheckOut = () => {
        let date = new Date();
        let time = date.toLocaleTimeString();
        let dates =
          date.getFullYear() +
          "-" +
          (date.getMonth() + 1) +
          "-" +
          date.getDate();
        let currentTime = [time, dates];

        setCheckOutTime(currentTime);
      };
    }
    setIsCheckedIn((prevState) => !prevState);
  };

  console.log(checkInTime.date);
  return (
    <div>
      <div className="container-9">
        <div className="nav">
          <Link className="logo" to="/">
            <img src={logo} id="logov" />
          </Link>
          <ul className=" list" type="none">
            <li>
              <Link className="li" to="/leave">
                Apply Leave
              </Link>
            </li>
            <li
              className={
                location.state.employerole === "doctor" ? "" : "hiiden"
              }
            >
              <Link className="li" to="/doctor">
                Doctor Visit
              </Link>
            </li>
            {console.log(location.state.employerole)}
            <li
              className={
                location.state.employerole === "FRONTLINE WORKER"
                  ? ""
                  : "hiiden"
              }
            >
              <Link className="li" to="/appoinment">
                OP entry
              </Link>
            </li>
            <li className={location.state.employerole === "HR" ? "" : "hiiden"}>
              <Link className="li" to="/employeregister">
                Add Employe
              </Link>
            </li>
            <li>
              <Link className="li" to="/editemploye">
                Edit Profile
              </Link>
            </li>
            {/* <li className="user">
              <i class="fa-solid fa-user"></i>
            </li> */}
            <li className="www">
              <select>
                <option>
                  {location.state.name} {location.state.employerole}
                </option>
                <option value="checkin">Check In </option>
                <option value="checkout" onClick={direct}>
                  Check Out
                </option>
              </select>
            </li>
            <li>
              {" "}
              {/* <div>
                <div>
                  <button
                    onClick={handleCheckIn}
                    disabled={checkInTime !== null}
                  >
                    Check In
                  </button>
                  {checkInTime && (
                    <span>
                      Check-in time: {checkInTime[0]}
                      Check-in date: {checkInTime[1]}
                    </span>
                  )}
                </div>
                <div>
                  <button
                    onClick={handleCheckOut}
                    disabled={checkOutTime !== null}
                  >
                    Check Out
                  </button>
                  {checkOutTime && (
                    <span>
                      Check-out time: {checkOutTime[0]}
                      Check-date: {checkOutTime[1]}
                    </span>
                  )}
                </div>
              </div> */}
              <button onClick={handleButtonClick}>
                {isCheckedIn ? "Check Out" : "Check In"}
              </button>
              {checkInTime && (
                <span>
                  Check-in time: {checkInTime[0]}
                  Check-in date: {checkInTime[1]}
                </span>
              )}
              {checkOutTime && (
                <span>
                  Check-out time: {checkOutTime[0]}
                  Check-date: {checkOutTime[1]}
                </span>
              )}
            </li>
          </ul>
        </div>
      </div>
      {<Optab />}
    </div>
  );
};

export default AdminPage;
