// export default Employe;
import React, { useEffect, useState } from "react";
// import logo from "../images/logoimg.jpeg";
import { InputText } from "primereact/inputtext";
import "./employelogin.css";
// import { Button } from 'primereact/button';
import { Link } from "react-router-dom";
import axios from "axios";
import { Button } from "primereact/button";
import { useNavigate } from "react-router-dom";

const Employe = () => {
  //value get
  const [employeid, setemployeid] = useState("");
  const [password, setpassword] = useState("");
  const [data, setdata] = useState([]);

  const [errorid, seterrorid] = useState("");
  const [er, seter] = useState("");

  const navigate = useNavigate();
  // api call
  useEffect(() => {
    axios
      .get("http://localhost:3002/register/employe")
      .then((res) => {
        setdata(res.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);
  function checkInHandler() {
    const main = employeid;
    const pass = password;
    const final = data.find((user) => user.empid === main);
    console.log(final.empid);
    // console.log();

    if (employeid === undefined || employeid === "") {
      seterrorid("Plese enter the Employ id");
    } else {
      seterrorid("");
    }

    if (final?.empid === main || final?.password === password) {
      navigate("/Admin", { state: final });
      sessionStorage.setItem("ID", employeid);
    } else {
      seter("Plese enter the Register Employ ID");
    }
  }

  return (
    <div className="employ">
      <div className="Sheet-1 row">
        <h1 className="col-lg-6">LOG IN </h1>

        <div className="col-lg-6 text-center">
          {/* <img src={logo} alt="" /> */}
        </div>

        <div className="row mt-5">
          <div className="employ-input">
            <label>Employe ID :</label> <br></br>
            <InputText
              className="Gender"
              type="text"
              value={employeid}
              onChange={(e) => setemployeid(e.target.value)}
            />
            <div className="errors">
              {errorid}
              {er}
            </div>
          </div>
          <div className="employ-input">
            <label>Password :</label> <br></br>
            <InputText
              className="Gender"
              value={password}
              onChange={(e) => setpassword(e.target.value)}
            />
            <div className="errors"></div>
          </div>
          <div className="employ-input mt-4">
            <Button
              className="Gender"
              severity="info"
              onClick={checkInHandler}
              raised
            >
              Check In
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Employe;
